    public void myInitComponents(Particular particular) {
        nombreInmuebleAlojamientoBoton.setText(particular.getNombre());
        
        ImageIcon imagen = new ImageIcon(particular.getFoto());
        //Se redimensiona
        ImageIcon imgRedimensionada = new ImageIcon(imagen.getImage().getScaledInstance(166, 109, 1));
        fotoBotonAlojamientoLabel.setIcon(imgRedimensionada);
    }




